import tkinter
import win32api
import base64
import os
import subprocess
import time
import sys
from tkinter import messagebox
from tkinter import filedialog

wind_ico = ""
def icon():
    tmp = open("tmp.ico","wb+")  
    tmp.write(base64.b64decode(wind_ico))
    tmp.close()
    tk.iconbitmap("tmp.ico")
    os.remove("tmp.ico")
tk = tkinter.Tk()
tk.title("Atswoo")
icon()
windowX = tk.winfo_screenwidth()
windowY = tk.winfo_screenheight()
cen_x = (windowX-400) / 2
cen_y = (windowY-225) / 2
tk.geometry('%dx%d+%d+%d' % (320,150, cen_x,cen_y))
tk.resizable(False,False)
lbl1 = tkinter.Label(tk, text="文件位置:")
lbl1.grid(column=0, row=0, padx=10, pady=1)
lbl2 = tkinter.Label(tk, text="设置秒数:")
lbl2.grid(column=0, row=1, padx=0, pady=0)
lbl3 = tkinter.Label(tk, text="循环次数:")
lbl3.grid(column=0, row=2, padx=0, pady=0)
et1_var = tkinter.StringVar()
et2_var = tkinter.StringVar()
et3_var = tkinter.StringVar()
et1 = tkinter.Entry(tk,textvariable=et1_var,width=20)
et1.grid(row=0, column=1, padx=0, pady=0,columnspan=50)
et2 = tkinter.Entry(tk,textvariable=et2_var,width=5)
et2.grid(row=1, column=1, padx=0, pady=0,columnspan=1)
et3 = tkinter.Entry(tk,textvariable=et3_var,width=5)
et3.grid(row=2, column=1, padx=0, pady=10,columnspan=1)
fpath = ""
def error():
    global ae
    global be
    global fe
    global a
    global b
    try:
        a = et2.get()
        a = int(a)
        if a > 0:
            good = True
            ae = 0
        else:
            tkinter.messagebox.showinfo( "Error:", "秒数是一个错误的值\n请尝试输入一个正整数")
            ae = 1
    except ValueError:
        tkinter.messagebox.showinfo( "Error:", "秒数是一个错误的值\n请尝试输入一个正整数")
        ae = 1
    try:
        b = et3.get()
        b = int(b)
        if b > 0:
            good = True
            be = 0
        else:
            tkinter.messagebox.showinfo( "Error:", "循环次数是一个错误的值\n请尝试输入一个正整数")
            be = 1
    except ValueError:
        tkinter.messagebox.showinfo( "Error:", "循环次数是一个错误的值\n请尝试输入一个正整数")
        be = 1
    if fpath == '':
        tkinter.messagebox.showinfo( "Error:", "文件路径为空")
        fe = 1
    else:
        fe = 0
def ready1():
    i = 0
    while i < b: 
        win32api.ShellExecute(0, 'open', fpath, '','',1) 
        time.sleep(a)
        p = "\"" + file + "\""
        taskkill = 'taskkill /f /im %s' % p
        subprocess.call(taskkill, shell=True, stdin=subprocess.PIPE, stdout=subprocess.PIPE, stderr=subprocess.PIPE)
        i += 1
def start():
    error(),
    if ae or be or fe == 1:
        return
    ready1()
    tkinter.messagebox.showinfo( "提示：", "执行完毕")
def click_button1():
    global fpath
    global dir
    global file
    fpath = filedialog.askopenfilename(title='选择一个exe文件', filetypes=[('Executable file', '*.exe'), ('All Files', '*')])
    et1_var.set(fpath)
    dir,file=os.path.split(fpath)
def click_button2():
    start()
def click_button3():
    tkinter.messagebox.showinfo( "帮助", "先按选择文件按钮选择一个exe文件。然后输入打开后多少秒关闭软件，再然后输入循环次数，最后按开始执行按钮，等待它自动执行完就好了。\n注：请不要在未响应时关闭程序，否则会被强行终止")
button1 = tkinter.Button(tk,text="选择文件",width=7, height=0,command=click_button1)
button1.grid(row=0, column=51, padx=10, pady=5)
button2 = tkinter.Button(tk,text="开始执行",width=10, height=0,command=click_button2)
button2.grid(row=3, column=1, padx=0, pady=5,columnspan=15)
button3 = tkinter.Button(tk,text="帮助",width=7, height=0,command=click_button3)
button3.grid(row=3, column=30, padx=10, pady=5)
tk.mainloop()